import React from "react";
import Link from "next/link";
import styled from 'styled-components'

import {AppBar, Grid, Toolbar, Typography} from '@material-ui/core'
import {useScroll} from "../../hooks/useScroll";

const Header = styled.header`
    background: transparent;
    position: fixed;
    top: 0;
    width: 100%;
    transition: .2s all;
  
    ${({active}) => active && `
        background: #fff;
        box-shadow: 0 1px 5px rgba(0, 0, 0, 0.25);
    `}
`

const Nav = styled(Grid) `
    
        
      .brand {
        color: #fff;
        text-transform: uppercase;
        font-family: 'Roboto', sans-serif;
        font-weight: 700;
        font-size: 25px;
        transition: .2s linear;
        scroll-behavior: smooth;
            ${({active}) => active && `
            color: #000;
        `}

      em {
        font-style: normal;
        color: #f7c552;  
        font-size: 32px;
      }
      
        
      
`

const menu = [
    'หน้าแรก','ทำไมต้องมากระบี่','บล็อก','แอพลิเคชั่น','ผู้จัดทำ'
]

export const Navbar = () => {

    const { offset, isScroll } = useScroll()



    return (
        <Header active={offset > 20}>
            <Toolbar>
                <Nav
                    container
                    justify={"space-between"}
                    className={'px-5'}
                    active={offset > 20 ? true : undefined}
                >
                    <Grid item xs={6} className={'brand'} >
                        <Link href={'/'}>
                            <a>
                                <em>w</em>
                                hykrabi
                            </a>
                        </Link>
                    </Grid>
                    <Grid item xs={6} className={'menu'}>
                        <ul className={'flex sm:flex-col md:flex-row justify-around items-center h-full'}>
                            {
                                menu.map(e => (
                                    <li
                                        className={`cursor-pointer ${offset > 20 ? 'text-black' : 'text-white'} active:scale-95 `}><a>{e}</a></li>
                                ))
                            }
                        </ul>
                    </Grid>
                </Nav>
            </Toolbar>
        </Header>
    )
}
