import React from 'react';
import styled from "styled-components";
import {useScroll} from "../hooks/useScroll";

const Parallax = styled.div`
  width: 100%;
  height: 100vh;
  background: url("./images/home/whykrabi-bg.jpg") center/cover no-repeat;
  vertical-align: middle;
  padding-top: 35vh;
`

const AboutKrabi = styled.section`
  width: 100%;
  height: 75vh;
  background: url("./images/home/sec-2.jpg") no-repeat;
  background-size: cover;
  background-attachment: fixed;
`

const Home = () => {

    const { offset } = useScroll()

    return (
        <>
            <section>
                <Parallax style={{backgroundPosition: `center calc(50% + ${offset * 0.5}px)`}} />
            </section>
            <AboutKrabi>
                <div className={'w-3/4 flex'}>
                    <div className={'flex flex-col'}>
                        d
                    </div>
                    <div className={'flex flex-col'}>
                        s
                    </div>
                    <div className={'flex flex-col'}>
                        a
                    </div>
                    <div className={'flex flex-col'}>
                        g
                    </div>
                </div>
            </AboutKrabi>
        </>
    );
}

export default Home;
