import React from "react";
import Link from "next/link";
import styled from 'styled-components'

import {AppBar, Grid, Toolbar, Typography} from '@material-ui/core'
import {useScroll} from "../../hooks/useScroll";

const Header = styled.header`
    background: transparent;
    position: fixed;
    top: 0;
    width: 100%;
    transition: .2s all;
  
    ${({active}) => active && `
        background: #fff;
        box-shadow: 0 1px 5px rgba(0, 0, 0, 0.25);
    `}
`

const Logo = styled(Grid) `
    color: #fff;
    text-transform: uppercase;
    font-family: 'Roboto', sans-serif;
    font-weight: 700;
    

      em {
        font-style: normal;
        color: #f7c552;  
        font-size: 32px;
      }
`

export const Navbar = () => {

    const { offset } = useScroll()



    return (
        <Header active={offset > 20}>
            <Toolbar>
                <Grid
                    container
                    justify={"space-between"}
                    className={'px-5'}
                >
                    <Logo item xs={6} active>
                        <span>
                            <em>w</em>
                            hykrabi
                        </span>
                    </Logo>
                    <Grid item xs={6}>
                        <ul className={'flex sm:flex-col md:flex-row justify-around float-right w-full'}>
                            <li className={'cursor-pointer'}><a>หน้าแรก</a></li>
                            <li className={'cursor-pointer'}><a>ทำไมต้องกระบี่</a></li>
                            <li className={'cursor-pointer'}><a>บล็อก</a></li>
                            <li className={'cursor-pointer'}><a>แอพพลิเคชั่น</a></li>
                            <li className={'cursor-pointer'}><a>ผู้จัดทำ</a></li>
                        </ul>
                    </Grid>
                </Grid>
            </Toolbar>
        </Header>
    )
}
