import React from 'react';
import styled from "styled-components";
import {useScroll} from "../hooks/useScroll";
import {Container} from "@material-ui/core";

const Parallax = styled.div`
  width: 100%;
  height: 100vh;
  background: url("./images/home/whykrabi-bg.jpg") center/cover no-repeat;
  vertical-align: middle;
  padding-top: 35vh;
`

const AboutKrabi = styled.section`
  width: 100%;
  height: 75vh;
  background: url("./images/home/sec-2.jpg") no-repeat;
  background-size: cover;
  background-attachment: fixed;
`

const Home = () => {

    const { offset } = useScroll()

    return (
        <>
            <section>
                <Parallax style={{backgroundPosition: `center calc(50% + ${offset * 0.5}px)`}} />
            </section>
            <AboutKrabi>
                <Container maxWidth={"md"} className={'flex justify-around items-center h-3/4 my-auto'}>
                    <div className={'flex flex-col text-white'}>
                        d
                    </div>
                    <div className={'flex flex-col text-white'}>
                        s
                    </div>
                    <div className={'flex flex-col text-white'}>
                        a
                    </div>
                    <div className={'flex flex-col text-white'}>
                        g
                    </div>
                </Container>
            </AboutKrabi>
        </>
    );
}

export default Home;
