import React from 'react';
import Image from "next/image";
import styled from "styled-components";

const LeftSide = styled.div`
    position: relative;
    padding: 3rem 1.8rem 1.5rem;
        
  :before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0,0,0,.4);
    z-index: -1;
  }
`



const Section3 = () => {

    return (
        <div className={'relative w-full h-100vh'}>
            <div className={'relative flex w-full h-full z-1'}>
                <LeftSide className={'w-1/3 text-white'}>
                    <h2 className={'text-2xl'}>แหล่งข้อมูลต่าง ๆ</h2>
                </LeftSide>
                <div className={'w-2/3 text-white'}>right</div>
            </div>
            <Image
                layout={'fill'}
                src={'/images/home/hotel-bg.jpg'}
                loading={"lazy"}
                objectFit={"cover"}
            />
        </div>
    );
}

export default Section3;
