import React from "react";
import Link from "next/link";
import styled from 'styled-components'

import {AppBar, Grid, Toolbar, Typography} from '@material-ui/core'
import {useScroll} from "../../hooks/useScroll";

const Header = styled.header`
    background: transparent;
    position: fixed;
    top: 0;
    width: 100%;
    transition: .2s all;
  
    ${({active}) => active && `
        background: #fff;
        box-shadow: 0 1px 5px rgba(0, 0, 0, 0.25);
    `}
`

const Nav = styled(Grid) `
    
        
      .brand {
        color: #fff;
        text-transform: uppercase;
        font-family: 'Roboto', sans-serif;
        font-weight: 700;
        font-size: 25px;
        transition: .2s linear;
        scroll-behavior: smooth;
            ${({active}) => active && `
            color: #000;
        `}

      em {
        font-style: normal;
        color: #f7c552;  
        font-size: 32px;
      }
      
        
      
`

export const Navbar = () => {

    const { offset } = useScroll()



    return (
        <Header active={offset > 20}>
            <Toolbar>
                <Nav
                    container
                    justify={"space-between"}
                    className={'px-5'}
                    active={offset > 20 ? true : undefined}
                >
                    <Grid item xs={6} className={'brand'} >
                        <Link href={'/'}>
                            <a>
                                <em>w</em>
                                hykrabi
                            </a>
                        </Link>
                    </Grid>
                    <Grid item xs={6} className={'menu'}>
                        <ul className={'flex sm:flex-col md:flex-row justify-around items-center h-full'}>
                            <li><a>หน้าแรก</a></li>
                            <li><a>ทำไมต้องกระบี่</a></li>
                            <li><a>บล็อก</a></li>
                            <li><a>แอพพลิเคชั่น</a></li>
                            <li><a>ผู้จัดทำ</a></li>
                        </ul>
                    </Grid>
                </Nav>
            </Toolbar>
        </Header>
    )
}
